sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	var getNome = new GlideAjax('NomeCommentoReview');
    getNome.addParam('sysparm_name', 'getNome');
    getNome.addParam('sysparm_number', g_form.getValue('id_utente_review'));
    getNome.getXML(populateNomeField);

	var getCognome = new GlideAjax('CognomeCommentoReview');
   getCognome.addParam('sysparm_name', 'getCognome');
   getCognome.addParam('sysparm_number', g_form.getValue('id_utente_review'));
   getCognome.getXML(populateCognomeField);


    function populateNomeField(response) {
        //le response contiene la risposta del server
        var nomeFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
        g_form.clearValue('nome_review');
        g_form.setValue('nome_review', nomeFromScriptInclude);

    }
	function populateCognomeField(response) {
        //le response contiene la risposta del server
        var cognomeFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
        g_form.clearValue('cognome_review');
        g_form.setValue('cognome_review', cognomeFromScriptInclude);

    }


}
