sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === ''|| newValue==oldValue) {
      return;
   }
  var getUtente = new GlideAjax('GetTipoMail');
    getUtente.addParam('sysparm_name', 'getUtente');
    getUtente.addParam('sysparm_number', g_form.getValue('utente'));
    getUtente.getXML(populateNomeField);

    function populateNomeField(response) {
        //le response contiene la risposta del server
        var nomeFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
        g_form.clearValue('mail_utente');
        g_form.setValue('mail_utente', nomeFromScriptInclude);
		}
	}
